    const {label,image,ingredients} = recipe || {}
    return (
        <Card maxW='sm'>
            <CardBody>
            <Image
            htmlHeight='150px'
            htmlWidth="300px"
            objectFit='cover'
      src={image}
      alt='Green double couch with wooden legs'
      borderRadius='lg'
    />
    </CardBody>

        </Card>
    )
}

export default RecipeCard;
