import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import RecipeList from './components/RecipeList';
import RecipeDetails from './components/RecipeDetails';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const fetchRecipes = async (query) => {

    const params = {
      q: query,
      field: ['uri', 'label', 'image', 'calories', 'yield', 'source', 'ingredientLines'],
      _cont: 'CHcVQBtNNQphDmgVQntAEX4BYUtzDAEBRGFFBmsWalZxBAMBUXlSV2AVZVJ1UQAPQGBICzRFZgB1VQYPQmVCUmQbZVd6BgcVLnlSVSBMPkd5BgNK',
      health: ['vegetarian', 'gluten-free'],
      diet: ['low-fat', 'low-sodium'],
      type: 'public',
      app_id: '55860e8a',
      app_key: '2c4615d71298e5457d4ca5d1ca38df8d'
    };
    try {
      const response = await axios.get('https://api.edamam.com/search', { params ,

      paramsSerializer: (params) => {
        return Object.entries(params).map(([key, value]) => {
          if (Array.isArray(value)) {
            return value.map(val => `${key}=${encodeURIComponent(val)}`).join('&');
          }
          return `${key}=${encodeURIComponent(value)}`;
        }).join('&');
      }});
      setRecipes(response.data.hits);
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
  };

  const handleRecipeSelect = (recipe) => {
    setSelectedRecipe(recipe);
  };

  return (
    <div className="container">
      <h1 className="text-center mt-3">Recipe Finder</h1>
      <SearchBar onSearch={fetchRecipes} />
      <div className="row mt-3">
        <div className="col-md-6">
          <RecipeList recipes={recipes} onRecipeSelect={handleRecipeSelect} />
        </div>
        <div className="col-md-6">
          {selectedRecipe && <RecipeDetails recipe={selectedRecipe} />}
        </div>
      </div>
    </div>
  );
}

export default App;
