import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBarR';
import RecipeList from './components/RecipeList';
import RecipeDetails from './components/RecipeDetails';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const fetchRecipes = async (query) => {
    try {
      const response = await axios.get(`https://api.edamam.com/search?q=${query}&app_id=YOUR_APP_ID&app_key=YOUR_APP_KEY`);
      setRecipes(response.data.hits);
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
  };

  const handleRecipeSelect = (recipe) => {
    setSelectedRecipe(recipe);
  };

  return (
    <div className="container">
      <h1 className="text-center mt-3">Recipe Finder</h1>
      <SearchBar onSearch={fetchRecipes} />
      <div className="row mt-3">
        <div className="col-md-6">
          <RecipeList recipes={recipes} onRecipeSelect={handleRecipeSelect} />
        </div>
        <div className="col-md-6">
          {selectedRecipe && <RecipeDetails recipe={selectedRecipe} />}
        </div>
      </div>
    </div>
  );
}

export default App;
